import "./Randomize.css"
import Casino from '@material-ui/icons/Casino';
import TransitionHover from './IconHovered'
import React, {Component} from 'react'
import ReactDOM from 'react-dom';


import defaults from '../../defaultMenuItems'
const options = defaults.to_arr(defaults.menu_items())

class Randomizer extends Component {
  constructor(props){
    super(props);

    this.updateValue = this.props.updateValue.bind(this)
    this.toggleMenuItem = this.props.toggleMenuItems.bind(this)
  }

  randomizeState(){
    console.log("randomizing")
    ReactDOM.unstable_batchedUpdates(() => {
      options.forEach(item => {
        if(Math.random() > .5){
          if(item.inputType === "slider"){
            console.log(item.title)
            this.updateValue(item.title,
                             Math.random()*(item.max - item.min) + item.min)
            this.toggleMenuItem(item.title)
          }
        }
      })
    })
  }

  render(){
    return (
      <div className="shaker">
        <TransitionHover>
          <Casino onClick={() => this.randomizeState()} color="secondary" />
        </TransitionHover>
      </div>
    );
  }
}

export default Randomizer;
